mapper.select:
SELECT

FROM bs_paper


        AND paper_name LIKE '%${paperName}%'


        AND flag_public = #{flagPublic}


        AND flag_edit = #{flagEdit}


        AND flag = #{flag}


        AND created_by = #{createdBy}

SELECT p.created_by
FROM bs_paper p
WHERE p.pk_paper = #{pkPaper}

SELECT p.flag_public
FROM bs_paper p
WHERE p.pk_paper = #{pkPaper}

SELECT p.flag_edit
FROM bs_paper p
WHERE p.pk_paper = #{fkPaper}

SELECT COUNT(1)
FROM bs_paper
WHERE paper_name = #{paperName}
